import styled from 'styled-components'

const Card=styled.div`
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    flex-basis: 20%;
    border-radius: 5%;
    border:1px solid black;
    margin:1%;
    padding:0.5%;
    font-size: 12px;
    color:black;
    background-color: white;
`
const QuoteIcon=styled.img`
    padding-top: 10px;
    width: 20px;
    height: 25px;
    `
const Image=styled.img`
    height: 32%;
    width: 100%;
    flex-direction: column;
    align-items: center;
    flex-wrap: wrap;
    align-content: center;
`

const Main=styled.div`
width:100%;
height:80%;
background-color:blue;
    border-radius: 15px;
    background-size: cover;
    padding-left: 2rem;
    /* -moz-transition: all .5s;
    transition: all .5s; */
    background-color:white;
    display:flex;
`

const SideTitle =styled.div`
margin-top: 2%;
margin-right: 5%;

`
const Container=styled.div`
    display: flex;
    flex-wrap: wrap;
    padding:2%
`





export {Card,Main,QuoteIcon,SideTitle,Image,Container};
